import React, { Component } from "react";

class App2 extends Component{
    state = {
        count : 0
    };
    increment = () =>{
        this.setState(current => {
            return {
                count : current + 1
            }
        })
    }
    render(){
        const { count } = this.state;
        return (<div>count</div>);
    }
}

export default App2;
