import React, { Component, useState } from "react";

const useInput = (initalValue) => {
    const [value, setValue] = useState(initalValue);
}

const App = () =>{
    const name = useInput("J.Doo");
    return (
        <div className="App">
            <h1>Hello</h1>
            <input placeholder="Name" value={name.value} /> 
        </div>);
}


export default App;
