import React, { Component } from "react";

class App2 extends Component{
    state = {
        count : 0
    };
    modify = (n) =>{
        this.setState({
            count : n
        });
    }
    render(){
        const { count } = this.state;
        return (<div><div>count</div><button onClick={() => this.modify(count+1)}>Increment</button></div>);
    }
}

export default App2;
